export declare type ActionType = {
    type: string;
    payload: any;
};
export declare type StateType = any;
declare type ReducerType = (state: StateType, action: ActionType) => StateType;
interface StoreInterface {
    state: StateType;
    reducer: ReducerType;
}
export declare type StoreType = StoreInterface | null | undefined;
declare const createStore: (initialState: StateType, reducer: ReducerType) => StoreType;
export default createStore;
